import express from "express";
import morgan from "morgan";
// Routes
import RutaArticulos from "./Routes/articulos";
import RutaMarcas from "./Routes/marcas";
import RutaCategorias from "./Routes/cateforias";

const app = express();

// Settings
app.set("port", 4000);

// Middlewares
app.use(morgan("dev"));
app.use(express.json());

// Routes
app.use("/api/articulos", RutaArticulos);

app.use("/api/articulos", RutaMarcas);

app.use("/api/articulos", RutaCategorias);


export default app;

