sourcesContent:
import { getConnection } from "../DataBase/database";

const getArticulos = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM articulos");
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const getArticulo= async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM articulos WHERE id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const addArticulo = async (req, res) => {
    try {
        const {Id,Codigo,Nombre,Descripcion,Precio,ImagenUrl,Fk_IdMarca,Fk_IdCategoria } = req.body;

        // if (name === undefined || programmers === undefined) {
        //     res.status(400).json({ message: "Bad Request. Please fill all field." });
        // }

        const articulo = {Id,Codigo,Nombre,Descripcion,Precio,ImagenUrl,Fk_IdMarca,Fk_IdCategoria };
        const connection = await getConnection();
        await connection.query("INSERT INTO articulos SET ?", articulo);
        res.json({ message: "Articulo agregado exitosamente!!" });
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const updateArticulo= async (req, res) => {
    try {
        const { id } = req.params;
        const {Id,Codigo,Nombre,Descripcion,Precio,ImagenUrl,Fk_IdMarca,Fk_IdCategoria } = req.body;

        // if (id === undefined || name === undefined || programmers === undefined) {
        //     res.status(400).json({ message: "Bad Request. Please fill all field." });
        // }

        const articulo = {Id,Codigo,Nombre,Descripcion,Precio,ImagenUrl,Fk_IdMarca,Fk_IdCategoria };
        const connection = await getConnection();
        const result = await connection.query("UPDATE articulos SET ? WHERE id = ?", [articulo, id]);
        
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const deleteArticulo = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM articulos WHERE id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

export const methods = {
    getArticulos,
    getArticulo,
    addArticulo,
    updateArticulo,
    deleteArticulo
};

